import styled from "styled-components";
import { FaTrash, FaEdit } from "react-icons/fa";
import { toast } from "react-toastify";
import { useRef } from "react";

const Table = styled.table`
    width: 100%;
    background: #fff;
    padding: 20px;
    box-shadow: 0px 0px 5px #ccc;
    border-radius: 5px;
    max-width: 800px;
    margin: 20px auto;
    word-break: break-all;
`;

export const Thead = styled.thead``;

export const Tbody = styled.tbody``;

export const Tr = styled.tr``;

export const Th = styled.th`
    text-align: start;
    border-bottom: inset;
    padding-bottom: 5px;

    @media (max-width: 500px) {
        ${(props) => props.onlyWeb && "display: none"}
    }
`;

export const Td = styled.td`
    padding-top: 15px;
    text-align: ${(props) => (props.alignCenter ? "center" : "start")};
    width: ${(props) => (props.width ? props.width : "auto")};

    @media (max-width: 500px) {
        ${(props) => props.onlyWeb && "display: none"}
    }
`;

const Grid = ({ users, setUsers, setOnEdit }) => {
    const ref = useRef();
    const user = ref.current;

    const handleEdit = (item) => {
        setOnEdit(item);
    };

    const handleDelete = async (id) => {
        const newArray = users.filter((user) => user.id !== id);

        let requestOptions = {
            method: "DELETE",
            headers: {
                "Content-Type": "application/json",
            },
            body: JSON.stringify(
                {
                    name: user.name.value,
                    email: user.email.value,
                    phone: user.phone.value,
                    date_birth: formattedDate
                }
            )
        }

        let response = await fetch("http://localhost:8800/" + id, requestOptions);

        let data = response.json();

        console.log(data);
        toast.success(data);
    }

    // const handleDelete = async (id) => {
    //     await fetch("http://localhost:8800/" + id)
    //     .then(({ data }) => {
    //         const newArray = users.filter((user) => user.id !== id);

    //         console.log(`New array value before setusers(newArray): ${newArray}.`);

    //         setUsers(newArray);
    //         console.log(`New setUsers(newArray) value before setusers(newArray): ${setUsers(newArray)}.`);

    //         toast.success(data);
    //     })
    //     .catch(({ data }) => toast.error(data));

    //     setOnEdit(null);
    // }

    return (
        <Table>
            <Thead>
                <Tr> 
                    <Th>Nome</Th>
                    <Th>E-mail</Th>
                    <Th /* onlyWeb */>Fone</Th>
                    <Th></Th>
                    <Th></Th>
                </Tr>
            </Thead>
            <Tbody>
                {users.map((item, i) => (
                    <Tr key={`item-${i}`}>
                        <Td width="30%">
                            {item.name}
                        </Td>
                        <Td width="30%">
                            {item.email}
                        </Td>
                        <Td /* alignCenter */ width="5%">
                            <FaEdit onClick={() => handleEdit(item)} />
                        </Td>
                        <Td /* alignCenter */ width="5%">
                            <FaTrash ref={ref} onClick={() => handleDelete(item.id)} />
                        </Td>
                    </Tr>
                ))}
            </Tbody>
        </Table>
    );
}
 
export default Grid;
